grid = []
for y in inp.splitlines():
    grid.append(list(y))

stars = []
for row in range(len(grid)):
    for col in range(len(grid[0])):
        if grid[row][col] == "#":
            stars.append((col,row))

cols = []
rows = []
for i in stars:
    c, r = i
    cols.append(c)
    rows.append(r)
occupied_cols = set(cols)
occupied_rows = set(rows)

stars2=[]
for i in range(len(stars)):
    x, y = stars[i]
    distx = x- len(list(filter(lambda a: a < x, occupied_cols)))
    disty = y- len(list(filter(lambda a: a < y, occupied_rows)))
    #print(x,y,x+distx,y+disty)
    stars[i]=(x+distx,y+disty)
    stars2.append((x+distx*999999,y+disty*999999))

dist = 0
sum_a = 0
for i in stars:
    for j in stars:
        x, y = i
        u, w = j
        dist = abs(x - u) + abs(y - w)
        sum_a += dist

sum_b = 0
for i in stars2:
    for j in stars2:
        x, y = i
        u, w = j
        dist = abs(x - u) + abs(y - w)
        sum_b += dist
print(sum_a // 2)
print(sum_b // 2)